mapper.insert:
insert into bbs_post (
	num,
	title, content,
	writer, password
) values (
	bbs_post_seq.nextval,
	#{title}, #{content},
	#{writer}, #{password}
)

mapper.select:

select *
from (
	select /*+ index_desc(bbs_post bbs_post_pk) */
		rownum rnum, num,
		title, writer,
		write_date
	from bbs_post
	where rownum <= #{endRow}
) where rnum >= #{startRow}

mapper.delete:
delete from bbs_post
where
	num = #{num}
	and password = #{password}
